import React, { Component } from "react";
import ImageCard from "./components/ImageCard";
import breeds from "./dogBreeds.json";
import Wrapper from "./components/Wrapper";
import Title from "./components/Title";
import { Col, Row, Container } from "./components/Grid";

class App extends Component {
  // Setting this.state.friends to the friends json array
  state = {
    breeds
  };

  changeClickStatus = id => {
    // Filter this.state.friends for friends with an id not equal to the id being removed
    const breedClicked = this.state.breeds.filter(dog => dog.id === id);
    breedClicked.clicked = true;
    // Set this.state.friends equal to the new friends array
    this.setState({ breeds });
    this.shuffle(breeds);
  };

  shuffle = (breeds) => {
    for (let i = breeds.length - 1; i > 0; i--) {
      const j = Math.floor(Math.random() * (i + 1));
      [breeds[i], breeds[j]] = [breeds[j], breeds[i]];
    }
    this.setState({ breeds });
  }

  // Map over this.state.friends and render a FriendCard component for each friend object
  render() {
    return (
      <Wrapper>
        {this.state.breeds.map(breed => (
          <ImageCard
          changeClickStatus={this.changeClickStatus}
          id={breed.id}
          name={breed.name}
          image={breed.image}
        />
        ))}
      </Wrapper> 
    );
  }
}

export default App;
